/*global jQuery:false */

(function ($) {
    "use strict";
	
    //*** bootstrap.js needs to be initialized first!
    if (typeof ($.fn.carousel) !== 'undefined') {
        
        // Set height
		var $carousel = $('#slider .carousel-inner');
		$carousel.css({ "height": $carousel.height() });
		
        $('#slider').on('slide.bs.carousel', function (e) {
			var nextSlide = $(e.relatedTarget).height();
			$(this).find('.active.item').parent().animate({ "height": nextSlide }, 500);
		});

    }

}(jQuery));